import React, { useState, useEffect } from 'react';
import { Play, Pause, RotateCcw, Volume2, VolumeX, Plus, Minus, Maximize, Minimize } from 'lucide-react';

function App() {
  const [seconds, setSeconds] = useState(240); // 4 minutes par défaut
  const [isActive, setIsActive] = useState(false);
  const [isPause, setIsPause] = useState(false);
  const [soundEnabled, setSoundEnabled] = useState(true);
  const [currentSet, setCurrentSet] = useState(1);
  const [totalSets, setTotalSets] = useState(5);
  const [isFullscreen, setIsFullscreen] = useState(false);

  useEffect(() => {
    let interval = null;

    if (isActive && seconds > 0) {
      interval = setInterval(() => {
        setSeconds(prev => prev - 1);
      }, 1000);
    } else if (seconds === 0) {
      if (soundEnabled) {
        const audio = new Audio('data:audio/wav;base64,');
        audio.play();
      }

      if (!isPause) {
        // Fin des 4 minutes d'exercice, passer aux 2 minutes de pause
        setSeconds(120);
        setIsPause(true);
        setIsActive(true); // Maintenir le timer actif
      } else {
        // Fin des 2 minutes de pause
        if (currentSet < totalSets) {
          // Passer à la série suivante
          setSeconds(240);
          setIsPause(false);
          setCurrentSet(prev => prev + 1);
          setIsActive(true); // Maintenir le timer actif
        } else {
          // Fin de toutes les séries
          setIsActive(false);
          setIsPause(false);
          setCurrentSet(1);
          setSeconds(240);
        }
      }
    }

    return () => clearInterval(interval);
  }, [isActive, seconds, isPause, currentSet, totalSets, soundEnabled]);

  const toggleTimer = () => setIsActive(!isActive);
  
  const resetTimer = () => {
    setIsActive(false);
    setSeconds(240);
    setIsPause(false);
    setCurrentSet(1);
  };

  const adjustTotalSets = (increment) => {
    setTotalSets(prev => Math.max(1, Math.min(20, prev + increment)));
  };

  const formatTime = (timeInSeconds) => {
    const minutes = Math.floor(timeInSeconds / 60);
    const seconds = timeInSeconds % 60;
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  const toggleFullscreen = () => {
    if (!document.fullscreenElement) {
      document.documentElement.requestFullscreen();
      setIsFullscreen(true);
    } else {
      document.exitFullscreen();
      setIsFullscreen(false);
    }
  };

  return (
    <div className={`flex flex-col items-center justify-center min-h-screen bg-gray-100 p-4 ${isFullscreen ? 'fixed inset-0 z-50' : ''}`}>
      <div className="bg-white rounded-lg shadow-xl p-6 md:p-8 w-full max-w-2xl">
        <div className="flex justify-end mb-4">
          <button
            onClick={toggleFullscreen}
            className="p-2 rounded-full hover:bg-gray-100"
          >
            {isFullscreen ? <Minimize size={24} /> : <Maximize size={24} />}
          </button>
        </div>

        <img 
          src="/images/logo.png" 
          alt="Ping Pong Club Airvault" 
          className="w-full max-w-md mx-auto mb-8" 
        />

        <div className="text-center mb-8">
          <h2 className="text-3xl md:text-4xl font-bold mb-4">
            {isPause ? "Pause" : "Exercice"}
          </h2>
          
          <div className="flex items-center justify-center gap-4 mb-6">
            <button 
              onClick={() => adjustTotalSets(-1)}
              disabled={isActive || totalSets <= 1}
              className="p-2 rounded-full bg-gray-200 hover:bg-gray-300 disabled:opacity-50"
            >
              <Minus size={20} />
            </button>
            <span className="text-xl md:text-2xl">
              Série {currentSet}/{totalSets}
            </span>
            <button 
              onClick={() => adjustTotalSets(1)}
              disabled={isActive || totalSets >= 20}
              className="p-2 rounded-full bg-gray-200 hover:bg-gray-300 disabled:opacity-50"
            >
              <Plus size={20} />
            </button>
          </div>

          <div className="text-7xl md:text-8xl font-mono font-bold mb-8">
            {formatTime(seconds)}
          </div>
        </div>

        <div className="flex justify-center gap-6">
          <button 
            onClick={toggleTimer}
            className="p-6 rounded-full bg-blue-500 text-white hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            {isActive ? <Pause size={32} /> : <Play size={32} />}
          </button>
          
          <button 
            onClick={resetTimer}
            className="p-6 rounded-full bg-gray-500 text-white hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500"
          >
            <RotateCcw size={32} />
          </button>

          <button
            onClick={() => setSoundEnabled(!soundEnabled)}
            className="p-6 rounded-full bg-gray-500 text-white hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500"
          >
            {soundEnabled ? <Volume2 size={32} /> : <VolumeX size={32} />}
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
