import React, { useState, useEffect } from 'react';
import { Play, Pause, RotateCcw, Volume2, VolumeX } from 'lucide-react';

const Logo = () => (
  <svg viewBox="0 0 1000 600" className="w-48 h-24 mb-4">
    <path d="M100 150 L900 150 L950 300 L900 450 L100 450 L50 300 L100 150" fill="none" stroke="black" strokeWidth="4"/>
    <g transform="translate(200, 250)">
      <path d="M0 0 C10,0 20,-10 30,-10 L40,-10 C50,-10 60,0 70,0" 
            fill="none" stroke="black" strokeWidth="8"/>
      <circle cx="35" cy="-5" r="15" fill="black"/>
    </g>
    <text x="300" y="280" fontSize="90" fontFamily="Arial Black" fontWeight="bold">PING PONG CLUB</text>
    <text x="300" y="380" fontSize="120" fontFamily="Arial Black" fontWeight="bold">AIRVAULT</text>
  </svg>
);

function App() {
  const [seconds, setSeconds] = useState(240); // 4 minutes
  const [isActive, setIsActive] = useState(false);
  const [isExercise, setIsExercise] = useState(true);
  const [soundEnabled, setSoundEnabled] = useState(true);
  const [currentSet, setCurrentSet] = useState(1);
  const [totalSets, setTotalSets] = useState(5);

  useEffect(() => {
    let interval = null;
    if (isActive && seconds > 0) {
      interval = setInterval(() => {
        setSeconds(seconds => seconds - 1);
      }, 1000);
    } else if (seconds === 0) {
      if (soundEnabled) {
        const audio = new Audio('data:audio/wav;base64,');
        audio.play();
      }

      if (isExercise) {
        setSeconds(120); // 2 minutes de repos
        setIsExercise(false);
      } else {
        if (currentSet < totalSets) {
          setSeconds(240); // 4 minutes d'exercice
          setIsExercise(true);
          setCurrentSet(prev => prev + 1);
        } else {
          setIsActive(false);
          setCurrentSet(1);
        }
      }
    }

    return () => clearInterval(interval);
  }, [isActive, seconds, isExercise, currentSet, totalSets, soundEnabled]);

  const toggleTimer = () => {
    setIsActive(!isActive);
  };

  const resetTimer = () => {
    setIsActive(false);
    setSeconds(240);
    setIsExercise(true);
    setCurrentSet(1);
  };

  const formatTime = (timeInSeconds) => {
    const minutes = Math.floor(timeInSeconds / 60);
    const seconds = timeInSeconds % 60;
    return `${minutes}:${seconds < 10 ? '0' : ''}${seconds}`;
  };

  return (
    <div className="flex flex-col items-center justify-center min-h-screen bg-gray-100">
      <div className="bg-white rounded-lg shadow-xl p-8 m-4 w-full max-w-md">
        <div className="text-center mb-6">
          <Logo />
          <div className="text-xl font-bold mb-2">
            {isExercise ? "Exercice" : "Repos"}
          </div>
          <div className="text-sm text-gray-600">
            Série {currentSet}/{totalSets}
          </div>
        </div>
        
        <div className="text-center mb-8">
          <div className="text-6xl font-mono font-bold mb-4">
            {formatTime(seconds)}
          </div>
        </div>

        <div className="flex justify-center space-x-4">
          <button 
            onClick={toggleTimer}
            className="p-4 rounded-full bg-blue-500 text-white hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            {isActive ? <Pause size={24} /> : <Play size={24} />}
          </button>
          
          <button 
            onClick={resetTimer}
            className="p-4 rounded-full bg-gray-500 text-white hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500"
          >
            <RotateCcw size={24} />
          </button>

          <button
            onClick={() => setSoundEnabled(!soundEnabled)}
            className="p-4 rounded-full bg-gray-500 text-white hover:bg-gray-600 focus:outline-none focus:ring-2 focus:ring-gray-500"
          >
            {soundEnabled ? <Volume2 size={24} /> : <VolumeX size={24} />}
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
